mapper.select:
SELECT approval_no, approval_status, (select name from user where user_id = approval.user_id) as name, date(created_at) as created_at, approval_title, (CASE approval_kind WHEN '1' THEN '연차신청' WHEN '2' THEN '업무지원요청' WHEN '3' THEN '예산신청' WHEN '4' THEN '출장신청' END) AS approval_kind, approval_note approval_kind, approval_note FROM approval
where approval_kind = ${ approval_kind }
ORDER BY created_at DESC

SELECT approval_no, approval_status, (select name from user where user_id = approval.user_id) as name, date(created_at) as created_at, approval_title, (CASE approval_kind WHEN '1' THEN '연차신청' WHEN '2' THEN '업무지원요청' WHEN '3' THEN '예산신청' WHEN '4' THEN '출장신청' END) AS approval_kind, approval_note approval_kind, approval_note FROM approval
where approval_kind = ${ approval_kind }
ORDER BY created_at DESC
